typedef struct {
    int layer_idx;
    int layer_type; // 0: input, 1: hidden, 2: output
    size_t inputs_size;
    size_t output_size;
    NNMatMulFunc mat_mul_func;
    NNMatTransposeFunc mat_transpose_func;
    NNActFuncType act_func_type;
    NNActFunc act_func;
    NNTensor *weights;
    NNTensor *biases;
    NNTensor *inputs;
} TestCaseLayer;

typedef struct {
    size_t batch_size;
    NNTensor *expected_outputs;
    NNTensorUnit output_tolerance;
    size_t n_layers;
    TestCaseLayer *layers;
} TestCase;

void run_test_cases(TestCase *test_cases, int n_cases, char *info) {
    for (int i = 0; i < n_cases; i++) {
        TestCase tc = test_cases[i];
        NNError error = {0};

        NNTensor *last_outputs = tc.layers[0].inputs;
        size_t last_outputs_size = tc.layers[0].output_size;

        // Iterate over the layers
        for (int j = 0; j < tc.n_layers; j++) {
            // Init the layer
            NNLayer *layer = nn_layer_init(tc.layers[j].inputs_size, tc.layers[j].output_size, &error);
            assert(layer != NULL);
            assert(error.code == NN_ERROR_NONE);

            if (tc.layers[j].layer_type != 0) {
                // Set weights
                const bool sw = nn_layer_set_weights(layer, tc.layers[j].weights, &error);
                assert(sw == true);
                assert(error.code == NN_ERROR_NONE);

                // Set biases
                const bool sb = nn_layer_set_biases(layer, tc.layers[j].biases, &error);
                assert(sb == true);
                assert(error.code == NN_ERROR_NONE);

                // Set matrix multiplication function
                const bool smf = nn_layer_set_mat_mul_func(layer, tc.layers[j].mat_mul_func, &error);
                assert(smf == true);
                assert(error.code == NN_ERROR_NONE);

                // Set matrix transpose function
                const bool stf = nn_layer_set_mat_transpose_func(layer, tc.layers[j].mat_transpose_func, &error);
                assert(stf == true);
                assert(error.code == NN_ERROR_NONE);
            }

            // Set activation function
            const bool saf = nn_layer_set_act_func(layer, tc.layers[j].act_func_type, tc.layers[j].act_func, &error);
            assert(saf == true);
            assert(error.code == NN_ERROR_NONE);

            // Init outputs tensor
            NNTensor *outputs = nn_tensor_init_NNTensor(2, (const size_t[]){tc.batch_size, tc.layers[j].output_size}, false, NULL, &error);
            assert(outputs != NULL);
            assert(error.code == NN_ERROR_NONE);

            // Compute
            const bool lfr = nn_layer_forward(layer, last_outputs, outputs, &error);
            assert(lfr == true);
            assert(error.code == NN_ERROR_NONE);

            last_outputs = outputs;
            last_outputs_size = tc.layers[j].output_size;

            // Cleanup
            nn_layer_destroy(layer);
        }

        // Check outputs
        for (int k = 0; k < tc.batch_size; k++) {
            for (int l = 0; l < last_outputs_size; l++) {
                // printf("expected=%f, got=%f\n", tc.expected_outputs->data[k * last_outputs_size + l], last_outputs->data[k * last_outputs_size + l]);
                assert(fabs(last_outputs->data[k * last_outputs_size + l] - tc.expected_outputs->data[k * last_outputs_size + l]) < tc.output_tolerance);
            }
        }
        printf("passed: %s case=%d info=%s\n", __func__, i + 1, info);
    }
}

